import {useState,useEffect} from 'react';
const Verify=()=>{
    const [email,setEmail]=useState("");
    const [code,setCode]=useState("");
    const [smsg,setSmsg]=useState("");
    const [emsg,setEmsg]=useState("");

    useEffect(function(){
        let x=localStorage.getItem("infos");
        x=JSON.parse(x);
        setEmail(x.email);
    },[]);
    const handleForm=(event)=>{
        event.preventDefault();
        // var credentials={email_ad:email,pass:password};
        // axios.post("http://localhost:8000/admin/api/login",credentials).then(function(rsp){
        //     setPost(rsp.data);
        // },function(err){
        //     setMsg("Some error occurred!");
        // });
        let x=localStorage.getItem("infos");
        x=JSON.parse(x);
        if(x.code===code)
        {
            setSmsg("Email successfully verified now you can login to your accout :)");
            setEmsg("");
        }
    }
    return(
        <form onSubmit={handleForm}>
            Verification code sent to:{email}
            <table>
                <tr>
                    <td>
                        Enter verification code:
                    </td>
                    <td>
                        <input type="text" onChange={(event)=>{setCode(event.target.value)}} value={code}></input>
                    </td>
                </tr>
                <tr>
                    <td>
                        <input type="submit" value="Verify"></input>
                    </td>
                </tr>
            </table>
        </form>
    )
}
export default Verify;
